    console.log("formvalue",formValues)
//    event.preventDefault();
}

    render(){

        const {handleSubmit, fields: {title, description}} = this.props;

        console.log(this.props);
        return ( <form  className="ui form"  onSubmit = { this.props.handleSubmit(this.onSubmit)} >
            <Field name="title"   type="text"        component={this.renderInput}  label = 'Enter Title' />
            <Field name="description"   type="text"   component={this.renderInput}  label = "Enter Description"   /> 
            <button  className="ui button primary">Submit</button>

        
        </form>)
    }
    


 

 

 

}

export default  reduxForm({         form:'streamCreate'  ,
fields: ['title', 'lastName', 'email']
})(StreamCreate);
