import streams from "../apis/streams";
import history from '../history'
import { SIGN_IN , SIGN_OUT ,
     CREATE_STREAM,
     FETCH_STREAMS,
     FETCH_STREAM,
     DELETE_STREAM,
     EDIT_STREAM
     } from "./types";
export const signIn = (userId) => {

    return {
        type : SIGN_IN,
        payload : userId
    }

};

export const signOut = () => {
    return {
        type : SIGN_OUT
    }
};

export const createStream = (formValues) => {
    return async (dispatch , getState) => {
            const {userId} = getState().auth ;
           const response = await streams.post('streams' , {...formValues, userId } );

           console.log("create stream",response.data);
           dispatch({
               type : 'CREATE_STREAM', payload : response.data
           });
           // programmatic navigation to get back user to route route
           history.push('/');
        } 
}


export const fetchStreams = () => {
    return async (dispatch) => {
           const response = await streams.get('streams');

           console.log(response.data);
           dispatch({
               type : 'FETCH_STREAMS', payload : response.data
           });
    } 
}


export const fetchStream = id => async dispatch => {
        const response = await streams.get(`/streams/${id}`);
        console.log(response.data);
        dispatch({
            type : 'FETCH_STREAM', payload : response.data
        });
}



export const editStream = (id, formValues) => async dispatch => {
    const response = await streams.patch(`/streams/${id}` , formValues);
        dispatch({      type : 'EDIT_STREAM', payload : response.data });
        history.push('/');
}


export const deleteStream = (id) => async dispatch => {
     await streams.delete(`/streams/${id}`);
        dispatch({
            type : 'DELETE_STREAM', payload : id
        });
        history.push('/');

 }
