import React, { PropTypes} from 'react';
import {Field, reduxForm} from 'redux-form';


class  StreamCreate extends React.Component{
  

    
    renderError = ({error , touched}) => {
        if(touched && error){
            return(
                <div className = "ui error message">
                        <div className="header">{error}</div>
                </div>
            )
        }

    }
  
  
  
    //  renderInput(  formProps    ){
    renderInput = (  {input , label , meta }    ) => {
        const className = `field ${meta.error }`
         return (
              <div className="field ">
                 <label> {label}  </label>
                <input {...input} type={input.type} className="" autoComplete = "off" />
                  {this.renderError(meta)}
            </div>

          //  {formProps.meta.touched && formProps.meta.error}      
          //  {/* <span>{formProps.meta.error}</span> */}
          // <input   {...formProps.input}   />   // jsx syntax 0f below syntax
          // <input   onChange ={formProps.input.onChange}  value= {formProps.input.value}  />
        )
    }
    onSubmit(  formvalues      ){




    console.log("input", formvalues )
//    event.preventDefault();
}

    render(){
    //    fields: {title, description}
        const {handleSubmit   } = this.props;

        console.log(this.props);
        return ( <form  className="ui form error"  onSubmit = { handleSubmit(this.onSubmit.bind(this))} >
            <Field name="title"   type="text"        component={this.renderInput}  label = 'Enter Title' />
            <Field name="description"   type="text"   component={this.renderInput}  label = "Enter Description"   /> 
            <button  className="ui button primary">Submit</button>

        
        </form>)
    }
    


 

 

 

}

const validate = (formValues) =>{
 const errors = {};

 if (!formValues.title ) {
    errors.title = 'You must enter a title';
 }
     
 if (!formValues.description ) {
    errors.description = 'You must enter a description';
 }

    return errors;
}

export default  reduxForm({         form:'streamCreate'  ,
// fields: ['title', 'description'] ,
validate : validate
})(StreamCreate);
