import React from 'react';


class GoogleAuth extends React.Component{

    state = {isSignedIn : null};
    componentDidMount(){
        window.gapi.load('client:auth2' , () =>{
            window.gapi.client.init({
                clientId : '548147403800-qhiek0431mroa1d8vv1n8rb65p6dvg2r.apps.googleusercontent.com' ,
                scope: 'email'
            }).then( () =>{

                this.auth = window.gapi.auth2.getAuthInstance();
                 this.setState({ isSignedIn : this.auth.isSignedIn.get()}) 
                
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = () =>{
        this.setState({ isSignedIn : this.auth.isSignedIn.get()}) ;
    }


    onSignIn= () =>{
            this.auth.onSignIn()
    }

    onSignOut = () => {
        this.auth.onSignOut()
    }
    renderAuthButton() {
    if  (this.state.isSignedIn === null){
        return null;
    } else  if (this.state.isSignedIn){
        return (<button onClick = {this.onSignIn} className="ui red google button"> // no parenthesis 
            <i className = "google icon"> SignOut </i>
            </button>
            )
    }
    return (<button className="ui red google button">
    <i className = "google icon">SignIn with Google</i>
    </button>
    )
}    
render(){ 
       return <div>   {this.renderAuthButton()}         </div>
}

}

export default GoogleAuth;
