import React , { PropTypes} from 'react';
import {Field, reduxForm} from 'redux-form';


class  StreamCreate extends React.Component{


    static propTypes = {
        handleSubmit: PropTypes.func,
        fields: PropTypes.object
      }


    renderInput(input ,label   ){
        console.log({label})
          return (
            <div className="field">
                <label>{input.label}</label>
                <input {...input}   />
            </div>
            // <input   {...formProps.input}   />   // jsx syntax 0f below syntax
            // <input   onChange ={formProps.input.onChange}  value= {formProps.input.value}  />
        )
    }
    onSubmit = (formValues) => {
       console.log("formvalue",formValues)
    }

    render(){
        
        const { fields: {title, description , email}, handleSubmit} = this.props;

        console.log(this.props);

        return ( 
                <form className="ui form"  onSubmit = { this.props. handleSubmit(onSubmit)} >
                        <Field name="title"   type="text"       label = {"Enter Title"}       component={this.renderInput} />
                        <Field name="description"   label = {"Enter Description"}   component={this.renderInput}    /> 

                        <label>Email</label>
                         <input type="email" placeholder="Email" {...email}/>


                        <button type="submit"  className="ui button primary">Submit</button>

                </form>
                )
    }
    
}

export default  reduxForm({    
         form:'streamCreate' ,
         fields: ['title', 'description']   

})(StreamCreate);
