import React, { PropTypes} from 'react';
import {Field, reduxForm} from 'redux-form';


class  StreamCreate extends React.Component{

    renderInput(  {input}    ){
         return (
            <div className="field">
                <label> {field.label}  </label>
            {/* <input {...input}   /> */}

            < input {...input} type={input.type} className="" />
                    {input.meta.touched && field.meta.error}      
                <span>{field.meta.error}</span>


            </div>
            // <input   {...formProps.input}   />   // jsx syntax 0f below syntax
            // <input   onChange ={formProps.input.onChange}  value= {formProps.input.value}  />
        )
    }
    onmySubmit({title, description} ){
    console.log("input", title,description )
//    event.preventDefault();
}

    render(){

        const {handleSubmit, fields: {title, description}} = this.props;

        console.log(this.props);
        return ( <form  className="ui form"  onSubmit = { handleSubmit(this.onmySubmit.bind(this))} >
            <Field name="title"   type="text"        component={this.renderInput}  label = 'Enter Title' />
            <Field name="description"   type="text"   component={this.renderInput}  label = "Enter Description"   /> 
            <button  className="ui button primary">Submit</button>

        
        </form>)
    }
    


 

 

 

}

export default  reduxForm({         form:'streamCreate'  ,
fields: ['title', 'description']
})(StreamCreate);
